import { useQuery } from "@tanstack/react-query";
import Axios from "axios";
import { useContext } from "react";
import { AppContext } from "../../App";

export const Temp = () => {

    const { city, formValid} = useContext(AppContext)

  const { data } = useQuery({
    queryKey: ["Weather"],
    queryFn: () => {
      return Axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=17cbc23e645646cd67cb8feea34b40f0`
      ).then((res) => {
        console.log(res.data);
        return res.data;
      });
    },
    enabled: formValid,
  });

  return (
      <div className="main">
        <div className="temperature">
          <div>
            {data && <p>{(Math.floor(data.main.temp))} C</p>}
          </div>
          <div></div>
        </div>
        <div className="otherDays"></div>
      </div>
  );
};
