import { useQuery } from "@tanstack/react-query";
import Axios from "axios";
import { useState } from "react";

export const Temp = () => {
  const [city, setCity] = useState("");

  const { data, isLoading } = useQuery({
    queryKey: ["Weather"],
    queryFn: () => {
      return Axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=17cbc23e645646cd67cb8feea34b40f0`
      ).then((res) => {
        console.log(res?.data);
      });
    },
  });

  const handleSubmit = (e) => {
    setCity(e.target.value)
    console.log(data);
  };

  if(city != '' && isLoading) {
    return <h1>Page is currently loading...</h1>
  }

  return (
    <div className="main">
      <div className="temperature">
        <div>
          {/* <form onSubmit={handleSubmit}> */}
            <input
              type="text"
              placeholder="Enter city name" 
            />
            <button onClick={handleSubmit} type="submit">Get Weather</button>
          {/* </form> */}
        </div>
        <div></div>
      </div>
      <div className="otherDays"></div>
    </div>
  );
};
