import { useQuery } from "@tanstack/react-query";
import Axios from "axios";
import { useState } from "react";

export const Temp = () => {
  const [formValid, setFormValid] = useState(false);

  const { data } = useQuery({
    queryKey: ["Weather"],
    queryFn: () => {
      return Axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=17cbc23e645646cd67cb8feea34b40f0`
      ).then((res) => {
        console.log(res.data);
        return res.data;
      });
    },
    enabled: formValid,
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (city.trim() !== "") {
      setFormValid(true);
    } else {
      setFormValid(false);
    }
  };

  return (
      <div className="main">
        <div className="temperature">
          <div>
            {data && <p>{JSON.stringify(Math.floor(data.main.temp))} C</p>}
          </div>
          <div></div>
        </div>
        <div className="otherDays"></div>
      </div>
  );
};
