import { useQuery } from "@tanstack/react-query";
import Axios from "axios";
import { useState } from "react";

export const Temp = () => {
  const [city, setCity] = useState("");

  const { data } = useQuery(["Weather"],() => {
      return Axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&units=metric&appid=17cbc23e645646cd67cb8feea34b40f0`
      ).then((res) => {
        console.log(res.data);
      });
    },);

  const handleSubmit = () => {
    console.log( data );
  };

  return (
    <div className="main">
      <div className="temperature">
        <div>
          {/* <form onSubmit={handleSubmit}> */}
            <input
              type="text"
              placeholder="Enter city name"
              value={city}
              onChange={(e)=>setCity(e.target.value)}/>
            <button onClick={handleSubmit} type="submit">Get Weather</button>
          {/* </form> */}
        </div>
        <div></div>
      </div>
      <div className="otherDays"></div>
    </div>
  );
};
