import { AppContext } from "../../App";
import { useContext, useState } from "react";

export const Temp = () => {

  function weatherImage() {
    const [image, setImage] = useState(() => {
      if(cityWeather === "Rain") {
        setImage({backgroundImage: `url("../../weatherIcons/Raining.gif")`})
      } else if (cityWeather === "Clouds" || cityWeather === "Clear") {
        setImage({backgroundImage: `url("../../weatherIcons/Sunny.gif")`})
      }
    });
  }


  const { cityHumidity, cityPressure, feelsLike, cityWind, cityTemp,cityWeather } = useContext(AppContext);

  return (
    <div className="main">
      <div className="temperature" style={{ image }}></div>
      <div className="extraInfo">
        <div>
          <p>{Math.floor(cityTemp) + " 째C" || ""}</p>
          <p></p>
        </div>
        <div>
          <p>{cityHumidity + " RH" || ""}</p>
          <p></p>
        </div>
        <div>
          <p>{cityPressure + " mB"}</p>
          <p></p>
        </div>
        <div>
          <p>{Math.floor(feelsLike) + " 째C" || ""}</p>
          <p></p>
        </div>
        <div>
          <p>{cityWind + " km/h" || ""}</p>
          <p></p>
        </div>
        <div>
          <p>{cityWeather +  "" || ""}</p>
          <p></p>
        </div>
      </div>
      <div className="otherDays"></div>
    </div>
  );
};
