import "./App.css";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { Navbar } from "./Navbar";
import { Home } from "./Pages/Home";
import { Contact } from "./Pages/Contact";
import { News } from "./Pages/News";
import { useState, createContext } from "react";
import Axios from "axios";

export const AppContext = createContext();

function App() {
  const [city, setCity] = useState("");
  const [cityHumidity, setCityHumidity] = useState("")
  const [cityWind, setCityWind] = useState("")
  const [cityWeather, setCityWeather] = useState("")
  const [cityPressure, setCityPressure] = useState("")


  const retrieveWeather = async (cityParameter) => {
    const response = await Axios.get(
      `https://api.openweathermap.org/data/2.5/weather?q=${cityParameter}&units=metric&appid=17cbc23e645646cd67cb8feea34b40f0`
    );
    return response.data;
  };

  const handleSubmit = async (e) => {
    try {e.preventDefault();
    const data = await retrieveWeather(city);
    console.log(data)
    setCityTemp(data.main.temp)
    setCityHumidity(data.main.humidity)
  } catch {
    alert("We do not have "+ city +" in our database")
  }
  };

  const [image, setImage] = useState({backgroundImage: `url("../../weatherIcons/Raining.gif")`})

  return (
    <AppContext.Provider value={{ city, setCity, handleSubmit, image, cityHumidity, cityPressure, cityWeather, cityWind }}>
